    }

    for(i in data.credits) {
      item = data.credits[i];

      if(!details[item.name]) details[item.name] = [item.value];
      else details[item.name].push(item.value);
    }

    for(i in details) {
      data[i] = details[i].join(',');
    }

    next(null, data);
  });
